import React from "react";
import { NewPlaneForm } from "./NewPlaneForm";


export const AddPlane = (props) => {

    const { newPlanes, updatePlane} = props;

    const deletePlane = () => {
        props.deletePlane(newPlanes);
    }

    // const createPlane = () => {
    //     props.createPlane(newPlanes);
    // }

    const addNewPlane = (newPlanes) => {
        console.log('inside addNewPlane in AddPlane.js');
		console.log('plane.name is: ' + newPlanes.name);
        return updatePlane({...newPlanes, planes: [...newPlanes]})
    };

    const planes = () => (
        <ul>
            {newPlanes.map((plane, index) => (
                <li key={index}>
                    <label>{`New Plane Name: ${plane.name}`}</label>
                    <button onClick={(e) => updatePlane(e.target.value)}>Add Plane</button>
                    <button onClick={(e) => deletePlane(e.target.value)}>Delete Plane</button>  
                </li>
            ))}
        </ul>
    )



    console.log({...newPlanes});


    return (
        <div>
             <h1>{newPlanes} <button onClick={deletePlane}>Delete Plane</button> </h1>
            {
            planes({ planes, planeId: newPlanes, deletePlane })
            }
            <NewPlaneForm addNewPlaner={addNewPlane} />

            {/* <h1>{plane} </h1>
            <button onClick={deletePlane}>Delete Plane</button> 
            <button onClick={addNewPlane}> add Plane</button> 
            <button onClick={createPlane}> add Plane</button> 
            <NewPlaneForm /> */}
        </div>
    );
};
