const PLANES_ENDPOINT = 'https://crudcrud.com/api/b2b918bfa3ab4151a89ad63e020e0470/planes';


class PlanesApi { // this class handles all the api calls
    get = async () => { 
        try {
            const resp = await fetch(PLANES_ENDPOINT);
            const data = await resp.json();
            return data;
        } catch(e) {
            console.log('Oops, looks like fetchPlanes had an issue.', e);
        }
    }

    create = async (plane) => {
        console.log("got inside create");
        console.log("at this point, house is: " + plane);
        console.log("json.stringify(house) is: ");
        console.log(JSON.stringify(plane));
        try{
            console.log(`${PLANES_ENDPOINT}`);
            const resp = await fetch(`${PLANES_ENDPOINT}/`, { 
            method: 'POST', // *GET, POST, PUT, DELETE, etc.
            mode: 'cors', // no-cors, *cors, same-origin
            // cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
            // credentials: 'same-origin', // include, *same
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                name: plane
            })
            });


            console.log("resp is: ", resp);
            return await resp.json();
        }

        catch(e){
            console.log('Oops, looks like creating/POST planes had an issue.');
            console.log('e is: ');
            console.log(e);
        }
    }

    put = async (plane) => {
        try {
            const resp = await fetch(`${PLANES_ENDPOINT}/${plane._id}`, {
                method: 'PUT',
                headers: {
                    'Content-type': 'application/json'
                },
                body: JSON.stringify(plane)
            });
            return await resp.json();
        } catch(e) {
            console.log('Oops, looks like updating planes had an issue.', e);
        }
    }

    delete = async(plane) => {
        try {
            const resp = await fetch(`${PLANES_ENDPOINT}/${plane._id}`, {
                method: 'DELETE',
                headers: {
                  'Content-Type': 'application/json'
                }
            });
            return resp;
        } catch(e) {
            console.log('Problem deleting plane.', e);
        }
    }

}

export const planesApi = new PlanesApi();
