        const updatedPlane = {
            ...plane,
            passengers: plane.passengers.filter((x) => x.id !== passengerId)
        };
        updatePlane(updatedPlane);
    }

    const addNewPassenger = (passenger) => {
        return updatePlane({...plane, passengers: [...plane.passengers, passenger]})
    };

    const passengers = () => (

        <ul>
            {plane.passengers.map((passenger, index) => (
                <li key={index}>
                    <label>{`Name: ${passenger.name} Area: ${passenger.area}`}</label>
                    <button onClick={(e) => deletePassenger(passenger.id)}>Delete</button>
                </li>
            ))}

        </ul>
    );

    // console.log(plane);
    console.log({...plane});
    console.log(plane.passengers);
    console.log(plane.passengers.area);
    return (
        <div>
            <h1>{plane.name} <button className="btn btn-danger" onClick={deletePlane}>Delete Plane</button> </h1>
            {
                passengers({ passengers, planeId: plane.id, deletePassenger })
            }
            <NewPassengerForm addNewPassenger={addNewPassenger} />
        </div>
    );
};








