import React from "react";
import { NewPlanForm } from './NewPlanForm';


export const Destination = (props) => {

    const { destination, updateDestination} = props;

    // console.log(destination);
    // console.log(destination.plans);

    const deleteDestination = () => {
        props.deleteDestination(destination);
    }

    const deletePlan = (plan) => {
        console.log('inside deletePlan function in Destination');
        const updatedDestination = {
            ...destination,
            plans: destination.plans.filter((x) => x !== plan) //removed id
        };
        console.log(plan, 'was deleted');
        updateDestination(updatedDestination);
    }

    const addNewPlan = (plan) => {
        console.log('inside addNewPlan in Destination.js');
		console.log('plan.name is: ' + plan.name);
		console.log('plan.seat is ' + plan.seat);
        return updateDestination({...destination, plans: [...destination.plans, plan]})
    };

    const plans = () => (  //creating plans
        <ul>
            {destination.plans.map((plan, index) => (
                <ul className="d-inline p-2" key={index}>
                    <h5>{`name: ${plan.name}, seat: ${plan.seat}`}</h5>
                    <button className="btn btn-danger btn-sm" onClick={(e) => deletePlan(plan)}>delete</button> 
                    {/* removed id from plan*/}
                </ul>
            ))}

        </ul>
    );


    return (
        <div>

        <div className="card">

            <div className="card-title text-center mt-3">
                <h1>{destination.model}</h1> <button className="btn btn-danger" onClick={deleteDestination}>delete destination</button>
            </div>

            <div className="card-body">
            {
                plans({ plans, destinationId: destination.id, deletePlan })
            }
            </div>

            <div className="card-footer text-center">
                <NewPlanForm addNewPlan={addNewPlan} />
            </div>
        </div>
        <br></br>
        </div>

    );
};
