import React from "react";
import { Plane } from './Plane';
import { destinationsApi } from "../rest/PlanesApi";
import { NewPlaneForm } from "./NewPlaneForm";


export class PlaneList extends React.Component {

    state = {
        destinations: [],
    };

    componentDidMount() {
        this.fetchPlanes();
    };

    fetchPlanes = async () => {
        const destinations = await destinationsApi.get();
        this.setState({ destinations });
    };

    createPlane = async (destination) => {
        await destinationsApi.post(destination);
        this.fetchPlanes();
    }

    updatePlane = async (destination) => {
        await destinationsApi.put(destination);
        this.fetchPlanes();
    };

    deletePlane = async (destination) => {
        await destinationsApi.delete(destination);
        this.fetchPlanes();
    }

    render() {
        return(
            <div className='container'>

                <div>
                    <NewPlaneForm createPlane={this.createPlane} />
                </div>

                <div>

                {this.state.planes.map((plane) => (
                    <Plane
                    plane={plane}
                    key={plane.id}
                    updatePlane={this.updatePlane}
                    deletePlane={this.deletePlane}
                    />
                ))}
                </div>

            </div>
        )
    }
}
