import React from "react";
import { NewPlanForm } from './NewPlanForm';


export const Trip = (props) => {

    const { trip, updateTrip} = props;

    // console.log(trip);
    // console.log(trip.plans);

    const deleteTrip = () => {
        props.deleteTrip(trip);
    }

    const deletePlan = (plan) => {
        console.log('inside deletePlan function in Trip');
        const updatedTrip = {
            ...trip,
            plans: trip.plans.filter((x) => x !== plan) //removed id
        };
        console.log(plan, 'was deleted');
        updateTrip(updatedTrip);
    }

    const addNewPlan = (plan) => {
        console.log('inside addNewPlan in Trip.js');
		console.log('plan.name is: ' + plan.name);
		console.log('plan.seat is ' + plan.seat);
        return updateTrip({...trip, plans: [...trip.plans, plan]})
    };

    const plans = () => (  //creating plans
        <ul>
            {trip.plans.map((plan, index) => (
                <ul className="d-inline p-2" key={index}>
                    <h5>{`name: ${plan.name}, seat: ${plan.seat}`}</h5>
                    <button className="btn btn-danger btn-sm" onClick={(e) => deletePlan(plan)}>delete</button> 
                    {/* removed id from plan*/}
                </ul>
            ))}

        </ul>
    );

//     <div class="card text-white bg-secondary mb-3" style="max-width: 20rem;">
//   <div class="card-header">Header</div>
//   <div class="card-body">
//     <h4 class="card-title">Secondary card title</h4>
//     <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
//   </div>

    return (
        <div>

        <div className="card text-white bg-secondary mb-3">

            <div className="card-title text-center mt-3">
                <h1>{trip.location}</h1> <button className="btn btn-danger" onClick={deleteTrip}>delete trip</button>
            </div>

            <div className="card-body">
            {
                plans({ plans, tripId: trip.id, deletePlan })
            }
            </div>

            <div className="card-footer text-center">
                <NewPlanForm addNewPlan={addNewPlan} />
            </div>
        </div>
        <br></br>
        </div>

    );
};
