import React from "react";
import { NewPassengerForm } from './NewPassangerForm';

export const Plane = (props) => {
    const { plane, updatePlane} = props;

    const deletePassenger = (passengerId) => {
        const updatedPlane = {
            ...plane,
            passengers: plane.passengers.filter((x) => x._id !== passengerId)
        };
        updatePlane(updatedPlane);
    }

    const addNewPassenger = (passenger) => {
        return updatePlane({...plane, passengers: [...plane.passengers, passenger]})
    };

    const passengers = () => (
        <ul>
            {plane.passengers.map((passenger, index) => (
                <li key={index}>
                    <label>{`${passenger.name} Area: ${passenger.area}`}</label>
                    <button onClick={(e) => deleteRoom(passenger._id)}>Delete</button>
                </li>
            ))}
        </ul>
    );

    return (
        <div>
            <h1>{plane.name}</h1>
            {
                passengers({ passengers, planeId: plane._id, deleteRoom })
            }
            <NewRoomForm addNewRoom={addNewRoom} />
        </div>
    );
};
