    constructor(props){
        super(props);
        this.state = {
            reviews: props.reviews
        }
    }

    handleCallback = (reviewData) => { this.setState({reviews: reviewData}) } // retrieves data from ReviewForm 


    render() { 

    let {reviews} = this.state;
