import React from "react";
import { Plane } from "./Plane";
import { PlaneList } from "./PlaneList";
import { planesApi } from "../rest/PlanesApi";
import { NewPassengerForm } from './NewPassengerForm';
import { NewPlaneForm } from "./NewPlaneForm";


export const AddPlane = (props) => {

    const { plane, updatePlane} = props;

    const deletePlane = () => {
        props.deletePlane(plane);
    }

    const createPlane = () => {
        props.createPlane(plane);
    }

    const addNewPlane = (plane) => {
        console.log('inside addNewPlane in AddPlane.js');
		console.log('plane.name is: ' + plane.name);
        return updatePlane({...plane, passengers: [...plane]})
    };

    const planes = () => (
        <ul>
            {plane.map((plane, index) => (
                <li key={index}>
                    <label>{`New Plane Name: ${plane.name}`}</label>
                    <button onClick={(e) => updatePlane(e.target.value)}>Add Plane</button>
                    <button onClick={(e) => deletePlane(e.target.value)}>Delete Plane</button>  
                </li>
            ))}
        </ul>
    )



    // console.log({...plane});


    return (
        <div>
            <h1>{plane} <button onClick={deletePlane}>Delete Plane</button> </h1>
            {
            planes({ planes, planeId: plane, deletePlane })
            }
            <NewPlaneForm addNewPlaner={addNewPlane} />

            <h1>{plane} </h1>
            <button onClick={deletePlane}>Delete Plane</button> 
            <button onClick={addNewPlane}> add Plane</button> 
            <button onClick={createPlane}> add Plane</button> 
            <NewPlaneForm />
        </div>
    );
};
