import React from "react";
import { Plane } from './Plane';
import { planesApi } from "../rest/PlanesApi";
import { NewPlaneForm } from "./NewPlaneForm";

export class PlaneList extends React.Component {

    state = {
        planes: [],
        planeChanged: false
    };

    componentDidMount() {
        this.fetchPlanes();
    };

    fetchPlanes = async () => {
        const planes = await planesApi.get();
        this.setState({ planes });
    };

    updatePlane = async (updatedPlane) => {
        await planesApi.put(updatedPlane);
        this.fetchPlanes();
    };

    addNewPlane = async (plane) => {
        console.log('inside addNewPlane in Plane.js');
        console.log('plane.name is: ' + plane.name);
            await planesApi.create(plane);
        };


    deletePlane = async (plane) => {
        await planesApi.delete(plane);
        // this.state.planeDeleted = true;
        this.fetchPlanes();
    }


    planes = () => (
        <ul>
            {this.state.planes.map((plane, index) => (
                <li key={index}>
                    <label>{`New Plane Name: ${plane.name}`}</label>
                    <button onClick={(e) => deletePassenger(plane)}>Delete Passenger</button> 
                </li>
            ))}
        </ul>
    )

    // addNewPlane = async (plane) => {
    //     console.log('inside addNewPlane in Plane.js');
    //     console.log('plane.name is: ' + plane.name);
    //     if (this.state.planes) {
    //         this.state.planes.push(<NewPlaneForm {...plane} />);}
    //     };


    render() {
        return(
            <div className='plane-list'>
                {/* {<NewPlaneForm addNewPlane={this.addNewPlane}/>} */}

                {this.state.planes.map((name) => (
                <NewPlaneForm
                value={name}
                key={name.id}
                updatePlane={this.updatePlane} 
                />
                ))}

                {this.state.planes.map((plane) => (
                    <Plane
                    plane={plane}
                    key={plane.id}
                    updatePlane={this.updatePlane}
                    // deletePlane={this.deletePlane}
                    />
                ))}
            </div>
        )
    }
}
