import React, {useState} from 'react';
import {planesApi} from '../rest/PlanesApi.js';

export const NewPlaneForm = (props) => {
	const[name, setName] = useState('');

	const onSubmit = (e) => {
		console.log('inside NewPlaneForm, onsubmit()');
		e.preventDefault();
		if (name){
		console.log('New Plane Name: ' + name);
		console.log('props: ', props);

		planesApi.create(name);

		setName('');

		} 
		else{
			console.log('invalid input');
		}
	};

    const deletePlane = async (plane) => {
        await planesApi.delete(plane._id);
        this.fetchPlanes();
    }

	return(
		<div>
			<h4>Add a new plane </h4>
			<form onSubmit={onSubmit}>
				<input
					type='text'
					placeholder='Name of New Plane'
					onChange={(e) => setName(e.target.value)}
					value={name}
				/>
				<button type='submit'>Add Plane</button>
                <button onClick={this.deletePlane}>Delete</button>
				</form>
			</div>
			)
};
