import React, {useState} from 'react';
import {planesApi} from '../rest/PlanesApi.js';




export const NewPlaneForm = (props) => {

	const[plane, setName] = useState('');

	// onTrigger = (event) => { 
    //     this.props.handleCallback(event.target.newReview.value); 
    //     event.preventDefault(); 
    // } 

	const onSubmit = (e) => {
		console.log('inside NewPlaneForm, onsubmit()');
		e.preventDefault();

		if (name){
		console.log('New Plane Name: ' + name);
		console.log('props: ', props);
        props.AddNewPlane({name});
        // planesApi.post();
        setName('');

		} 
		else{
			console.log('invalid input');
		}
	};

	return(
		<div>
			<h4>Add a new plane </h4>
			<form onSubmit={onSubmit}>
				<input
                type='text'
                placeholder='name'
                onChange={(e) => setName(e.target.value)}
                value={name}
				/>
				<button type='submit' value='name' name='newPlane'>Create New Plane</button>
				</form>
			</div>
			)
};
