import React, { useState } from "react";

export const NewDestinationForm = (props) => {
    const [name, setName] = useState('');
    const[seat, setArea] = useState('');
    const[date, setDate] = useState('');

    const onSubmit = (e) => {
        e.preventDefault();
        if (name && seat) {
            console.log('New Destination Name: ' + name + seat);
            props.addNewDestination({name, seat, date});
            setName('');
            setArea('');
            setDate('');
        } else {
            console.log('invalid input');
        }
    };

    return (
        <div>
            <h4>add the date of when you wish to complete this bucket list item by:</h4>

            <div className="card text-white bg-primary my-5">
			<div className="text-center mt-4">
			<h1>Create your bucket list here</h1>
			</div>

			<div className="card-body text-center">
			<form onSubmit={onSubmit}>
				<input
                    className="form-control form-control-lg" type="text" placeholder=".form-control-lg"
					type='text'
					placeholder='enter dream destination'
					onChange={(e) => setLocation(e.target.value)}
					value={location}
				/>
				<button className="btn btn-success mt-3" type='submit' value='submit'>add to list</button>
				</form>
			</div>
			<br></br>

			</div>



            <form className="d-flex align-items-end" onSubmit={onSubmit}>
                <input
                className="form-control form-control-sm" type="text" placeholder=".form-control-sm" id="inputSmall"
                type='text'
                placeholder='name'
                onChange={(e) => setName(e.target.value)}
                value={name}
                />
                <input
                className="form-control form-control-sm" type="text" placeholder=".form-control-sm" id="inputSmall"
                type='text'
                placeholder='seat'
                onChange={(e) => setArea(e.target.value)}
                value={seat}
                />
                <input
                className="form-control form-control-sm" type="text" placeholder=".form-control-sm" id="inputSmall"
                type='text'
                placeholder='date'
                onChange={(e) => setDate(e.target.value)}
                value={date}
                />
                <button className='btn btn-primary btn-sm m-1' type='submit'>+</button>
            </form>
        </div>
    )
}
