import React from "react";
import { House } from './Plane';
import { planesApi } from "../rest/PlanesApi";

export class HouseList extends React.Component {
    state = {
        planes: []
    };

    componentDidMount() {
        this.fetchHouses();
    };

    fetchHouses = async () => {
        const planes = await planesApi.get();
        this.setState({ planes});
    };

    updateHouse = async (updatedHouse) => {
        await planesApi.put(updatedHouse);
        this.fetchHouses();
    };

    render() {
        return(
            <div className='plane-list'>
                {this.state.planes.map((plane) => (
                    <House
                    plane={plane}
                    key={plane._id}
                    updateHouse={this.updateHouse}
                    />
                ))}
            </div>
        )
    }
}
