import React from "react";
import { NewPassengerForm } from './NewPassengerForm';

export const Plane = (props) => {

    const { plane, updatePlane} = props;

    console.log(plane);
    console.log(updatePlane);

    const deletePlane = (plane) => {
        const removedPlane = {

        }
        this.deletePlane(plane._id);
    }

    const deletePassenger = (passengerId) => {
        const updatedPlane = {
            ...plane,
            passengers: plane.passengers.filter((x) => x._id !== passengerId)
        };
        updatePlane(updatedPlane);
    }

    const addNewPassenger = (passenger) => {
        return updatePlane({...plane, passengers: [...plane.passengers, passenger]})
    };

    const passengers = () => (
        <ul>
            {plane.passengers.map((passenger, index) => (
                <li key={index}>
                    <label>{`Name: ${passenger.name} Area: ${passenger.area}`}</label>
                    <button onClick={(e) => deletePassenger(passenger._id)}>Delete</button>
                </li>
            ))}
        </ul>
    );

    return (
        <div>
            <h1>{plane.name}</h1>
            {/* <button className="btn btn-danger" onClick={deletePlane}>Delete Plane</button> */}
            <button onClick={(e) => deletePlane(plane._id)}>Delete</button>
            {
                passengers({ passengers, planeId: plane._id, deletePassenger })
            }
            <NewPassengerForm addNewPassenger={addNewPassenger} />
        </div>
    );
};
