import React, { useState } from "react";

export const NewPlanForm = (props) => {
    const [experience, setExperience] = useState('');
    const[budget, setArea] = useState('');

    const onSubmit = (e) => {
        e.preventDefault();
        if (experience && budget) { // if experience and budget have a value this will be true
            console.log('New Plan Experience: ' + experience + budget);
            props.addNewPlan({experience, budget});
            setExperience('');
            setArea('');
        } else {
            console.log('invalid input');
        }
    };

    return (
        <div>
            <h4>add new plan</h4>
            <form className="mb-3" onSubmit={onSubmit}>
                <input

                type='text'
                placeholder='experience'
                onChange={(e) => setExperience(e.target.value)}
                value={experience}
                />
                <input
                type='text'
                placeholder='budget'
                onChange={(e) => setArea(e.target.value)}
                value={budget}
                />
                <button className='btn btn-primary btn-sm m-1' type='submit'>+</button>
            </form>
        </div>
    )
}
