import React, {useState} from 'react';

export const NewBucketForm = (props) => {
	const[location, setLocation] = useState('');

	const onSubmit = (e) => {
		console.log('inside NewBucketForm');
		e.preventDefault();

		if (location){
		console.log('New Bucket Name: ' + location);
		console.log('props: ', props);
		props.createBucket({location});
        setLocation('');

		} 
		else{
			console.log('invalid input');
		}
	};

	return(
		<div className="card m-5">
			<div className="card-header text-center">
			<h1>My Bucket List.</h1>
			</div>

			<div className="card-body text-center">
			<form onSubmit={onSubmit}>
				<input
					type='text'
					placeholder='add destination'
					onChange={(e) => setLocation(e.target.value)}
					value={location}
				/>
				<button className="btn btn-primary m-3" type='submit' value='submit'>+ to bucket</button>
				</form>
			</div>
			<br></br>

			</div>
	)
};
