import React from "react";
import { planesApi } from "../rest/PlanesApi";
import { NewPassengerForm } from './NewPassengerForm';


export const Plane = (props) => {

    const { plane, updatePlane} = props;

    const deletePlane = async (id) => {
        console.log(`Delete House Click for ${plane.id} ${plane.name}`); 
        const updatedPlane = {
            ...plane,
            id: plane.ids.filter((x) => x !== id) //removed id
        };
        // console.log(passenger); // the passenger that was deleted
        updatePlane(updatedPlane);
        // return updatePlane(planesApi.delete(plane))
        // console.log(plane);
        // updatePlane(deletedPlane);
    };

    const deletePassenger = (passenger) => {
        console.log('inside deletePassenger function in Plane');
        // console.log(passengerId);
        const updatedPlane = {
            ...plane,
            passengers: plane.passengers.filter((x) => x !== passenger) //removed id
        };
        console.log(passenger); // the passenger that was deleted
        updatePlane(updatedPlane);
    }

    const addNewPassenger = (passenger) => {
        console.log('inside addNewPassenger in Plane.js');
		console.log('passenger.name is: ' + passenger.name);
		console.log('passenger.area is ' + passenger.area);
        return updatePlane({...plane, passengers: [...plane.passengers, passenger]})
    };

    const passengers = () => (  //creating passengers
        <ul>
            {plane.passengers.map((passenger, index) => (
                <li key={index}>
                    <label>{`Name: ${passenger.name} Area: ${passenger.area}`}</label>
                    <button onClick={(e) => deletePassenger(passenger)}>Delete</button> 
                    {/* removed id from passenger prop */}
                </li>
            ))}

        </ul>
    );

    // console.log({...plane});
    console.log({...plane.id});


    return (
        <div>
            <h1>{plane.name} <button onClick={deletePlane}>Delete Plane</button> </h1>
            {
                passengers({ passengers, planeId: plane.id, deletePassenger })
            }
            <NewPassengerForm addNewPassenger={addNewPassenger} />
        </div>
    );
};
