import React from "react";
import { NewDestinationForm } from './NewDestinationForm';


export const Bucket = (props) => {

    const { bucket, updateBucket} = props;

    // console.log(bucket);
    // console.log(bucket.destinations);

    const deleteBucket = () => {
        props.deleteBucket(bucket);
    }

    const deleteDestination = (destination) => {
        console.log('inside deleteDestination function in Bucket');
        const updatedBucket = {
            ...bucket,
            destinations: bucket.destinations.filter((x) => x !== destination) //removed id
        };
        console.log(destination, 'was deleted');
        updateBucket(updatedBucket);
    }

    const addNewDestination = (destination) => {
        console.log('inside addNewDestination in Bucket.js');
		console.log('destination.name is: ' + destination.name);
		console.log('destination.seat is ' + destination.seat);
        return updateBucket({...bucket, destinations: [...bucket.destinations, destination]})
    };

    const destinations = () => (  //creating destinations
        <ul>
            {bucket.destinations.map((destination, index) => (
                <ul className="d-inline p-2" key={index}>
                    <h5>{`name: ${destination.name}, seat: ${destination.seat}`}, </h5>
                    <button className="btn btn-danger btn-sm" onClick={(e) => deleteDestination(destination)}>delete</button> 
                    {/* removed id from destination*/}
                </ul>
            ))}

        </ul>
    );


    return (
        <div>

        <div className="card">

            <div className="card-title text-center mt-3">
                <h1>{bucket.location}</h1> <button className="btn btn-danger" onClick={deleteBucket}>delete bucket</button>
            </div>

            <div className="card-body">
            {
                destinations({ destinations, bucketId: bucket.id, deleteDestination })
            }
            </div>

            <div className="card-footer text-center">
                <NewDestinationForm addNewDestination={addNewDestination} />
            </div>
        </div>
        <br></br>
        </div>

    );
};
