import React, {useState} from 'react';
import {planesApi} from '../rest/PlanesApi.js';


export const NewPlaneForm = (props) => {
	const[model, setModel] = useState('');

	const onSubmit = (e) => {
		console.log('inside NewPlaneForm');
		e.preventDefault();

		if (model){
		console.log('New Plane Name: ' + model);
		console.log('props: ', props);
		props.createNewPlane({model});
        // props.AddNewPlane({plane});
        // planesApi.post(plane);
        setModel('');

		} 
		else{
			console.log('invalid input');
		}
	};

	return(
		<div>
			<h4>Add a new plane </h4>
			<form onSubmit={onSubmit}>
				<input
					type='text'
                    key={model.id}
					placeholder='ENTER NEW PLANE'
					onChange={(e) => setModel(e.target.value)}
					value={model}
				/>
				<button type='submit' value='submit'>Create New Plane</button>
				</form>
			</div>
			)
};
