import React from "react";
import { Destination } from './Destination';
import { tripsApi } from "../rest/DestinationsApi";
import { NewDestinationForm } from "./NewDestinationForm";


export class DestinationList extends React.Component {

    state = {
        trips: [],
    };

    componentDidMount() {
        this.fetchDestinations();
    };

    fetchDestinations = async () => {
        const trips = await tripsApi.get();
        this.setState({ trips });
    };

    createDestination = async (trip) => {
        await tripsApi.post(trip);
        this.fetchDestinations();
    }

    updateDestination = async (trip) => {
        await tripsApi.put(trip);
        this.fetchDestinations();
    };

    deleteDestination = async (trip) => {
        await tripsApi.delete(trip);
        this.fetchDestinations();
    }

    render() {
        return(
            <div className='container'>

                <div>
                    <NewDestinationForm createDestination={this.createDestination} />
                </div>

                <div>

                {this.state.trips.map((trip) => (
                    <Destination
                    trip={trip}
                    key={trip.id}
                    updateDestination={this.updateDestination}
                    deleteDestination={this.deleteDestination}
                    />
                ))}
                </div>

            </div>
        )
    }
}
