import React, {useState} from 'react';
import {planesApi} from '../rest/PlanesApi.js';

export const NewPlaneForm = (props) => {
	const[name, setName] = useState('');

	const onSubmit = (e) => {
		console.log('inside NewPlaneForm, onsubmit()');
		e.preventDefault();

		if (name){
		console.log('New Plane Name: ' + name);
		console.log('props: ', props);
        props.addNewPlane({name});
        setName('');

		// planesApi.create(name);
        // props.createPlane({name});

		setName('');
		} 
		else{
			console.log('invalid input');
		}
	};

	return(
		<div>
			<h4>Add a new plane </h4>
			<form onSubmit={onSubmit}>
				<input
					type='text'
					placeholder='Name of New Plane'
                    // key={id}
					onChange={(e) => setName(e.target.value)}
					value={name}
				/>
				<button type='submit'>Add Plane</button>
				</form>
			</div>
			)
};
