import React from "react";
import { Destination } from './Destination';
import { destinationsApi } from "../rest/DestinationsApi";
import { NewDestinationForm } from "./NewDestinationForm";


export class DestinationList extends React.Component {

    state = {
        destinations: [],
    };

    componentDidMount() {
        this.fetchDestinations();
    };

    fetchDestinations = async () => {
        const destinations = await destinationsApi.get();
        this.setState({ destinations });
    };

    createDestination = async (destination) => {
        await destinationsApi.post(destination);
        this.fetchDestinations();
    }

    updateDestination = async (destination) => {
        await destinationsApi.put(destination);
        this.fetchDestinations();
    };

    deleteDestination = async (destination) => {
        await destinationsApi.delete(destination);
        this.fetchDestinations();
    }

    render() {
        return(
            <div className='container'>

                <div>
                    <NewDestinationForm createDestination={this.createDestination} />
                </div>

                <div>

                {this.state.destinations.map((destination) => (
                    <Destination
                    destination={destination}
                    key={destination.id}
                    updateDestination={this.updateDestination}
                    deleteDestination={this.deleteDestination}
                    />
                ))}
                </div>

            </div>
        )
    }
}
