import React, {useState} from 'react';
import { NewPassengerForm } from './NewPassengerForm.js';
import {planesApi} from '../rest/PlanesApi.js';




export const NewPlaneForm = (props) => {
	const[plane, setName] = useState('');
	// const[plane, setName] = useState('');

	// onTrigger = (event) => { 
    //     this.props.handleCallback(event.target.newReview.value); 
    //     event.preventDefault(); 
    // } 

	const onSubmit = (e) => {
		console.log('inside NewPlaneForm, onsubmit()');
		e.preventDefault();

		if (plane){
		console.log('New Plane Name: ' + plane);
		console.log('props: ', props);
        // props.AddNewPlane({name});
        planesApi.post();
        // setName('');

		} 
		else{
			console.log('invalid input');
		}
	};

	return(
		<div>
			<h4>Add a new plane </h4>
			<form onSubmit={onSubmit}>
				<input
					type='text'
                    // key={name.id}

					placeholder='ENTERNEW PLACE'
					onChange={(e) => setName(e.target.value)}
					value={plane}
				/>
				<button type='submit' value='submit' name='newReview'>Create New Plane</button>
				</form>
			</div>
			)
};




